function addTestFrame() {
  // Create a simple sample frame with a proper test image
  // This creates a simple red border frame (500x888, 9:16 ratio)
  const sampleFrame = {
    id: `test-frame-${Date.now()}`,
    name: "Test Custom Frame",
    description: "Sample frame untuk testing",
    category: "custom",
    maxCaptures: 3,
    duplicatePhotos: false,
    // Simple 500x888 frame with RED border (9:16 ratio)
    // Canvas with white background and red border
    imagePath:
      "data:image/png;base64,",
    thumbnailUrl:
      "data:image/png;base64,",
    slots: [
      {
        id: "slot_1",
        left: 0.1,
        top: 0.15,
        width: 0.35,
        height: 0.2,
        aspectRatio: "4:5",
        zIndex: 2,
        photoIndex: 0,
      },
      {
        id: "slot_2",
        left: 0.55,
        top: 0.15,
        width: 0.35,
        height: 0.2,
        aspectRatio: "4:5",
        zIndex: 2,
        photoIndex: 1,
      },
      {
        id: "slot_3",
        left: 0.325,
        top: 0.45,
        width: 0.35,
        height: 0.2,
        aspectRatio: "4:5",
        zIndex: 2,
        photoIndex: 2,
      },
    ],
    layout: {
      aspectRatio: "9:16",
      orientation: "portrait",
      backgroundColor: "#ffffff",
    },
    views: 0,
    uses: 0,
    likes: 0,
    createdBy: "test@fremio.com",
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString(),
  };

  // Get existing frames
  const frames = JSON.parse(localStorage.getItem("custom_frames") || "[]");

  // Add sample frame
  frames.push(sampleFrame);

  // Save to localStorage
  localStorage.setItem("custom_frames", JSON.stringify(frames));

  console.log("âœ… Test frame added!");
  console.log("Frame ID:", sampleFrame.id);
  console.log("Total frames:", frames.length);
  console.log("");
  console.log("ðŸ”„ Refresh halaman /frames untuk melihat frame baru!");

  return sampleFrame;
}

// Auto add frame jika belum ada
const currentFrames = JSON.parse(localStorage.getItem("custom_frames") || "[]");
if (currentFrames.length === 0) {
  console.log("ðŸ“¦ No custom frames found. Adding test frame...");
  addTestFrame();
  console.log("âœ… Test frame added! Refresh halaman /frames");
} else {
  console.log("âœ… Custom frames already exist:", currentFrames.length);
  console.log("Run addTestFrame() to add more test frames");
}
